import React,{ Component } from 'react'

export default class Com extends Component{
  constructor(props){ 
    super(props)
    this.state={
      com:[]
    }
    async function helloAsync(props){
      const mmm = await props.comment_info.comment_content
      return mmm
    }
    helloAsync().then((mmm)=>{
      this.setState(
        {com:mmm},()=>{console.log('bbb',this.state.com)}
      )
    })
  }
  render(){
    // console.log(this.props.com.comment_info.comment_content)
    // const comment_content = this.props.comment_content.comment_content
    // console.log(comment_content)
    
    return (
    <div>{this.state.com}</div>
    // <div></div>
    )
  }
  
}
