import React, { useState, useEffect } from "react";
import ApiArt from '../ApiArt'

const THRESHOLD = 15;
 
const SlidingWindowScrollHook = (props) =>  {
  const [start, setStart] = useState(0);
  const [llist, setLlist] = useState([{id:1},{id:2},{id:3},{id:4},{id:5},{id:6},{id:7},{id:8},{id:9},{id:10},{id:11},{id:12},{id:13},{id:14},{id:15}]);
  const [end, setEnd] = useState(THRESHOLD);
  // const [observer, setObserver] = useState(null);
  const [scrollHeight, setScrollHeight] = useState({});
  const [scrollH, setScrollH] = useState({});
  const [more, setMore] = useState(0);
  const [num, setNum] = useState(0);
  const [showcate2, setShowcate2] = useState(props.showcate2)
  console.log('scroll');
  const ScollPostion = () => {
    let t, h
    // , l, w

    if (document.documentElement && document.documentElement.scrollTop) {
      t = document.documentElement.scrollTop
      // l = document.documentElement.scrollLeft
      // w = document.documentElement.scrollWidth
      h = document.documentElement.scrollHeight
    } else if (document.body) {
      t = document.body.scrollTop
      // l = document.body.scrollLeft
      // w = document.body.scrollWidth
      h = document.body.scrollHeight
    }
    return {
      top: t,
      // left: l,
      // width: w,
      height: h,
    }
  }
  
  useEffect(() => {
    document.documentElement.addEventListener('touchmove', (e) => {
      const timer = setTimeout((_) => {
        const postion = ScollPostion()
        // console.log('滑动距离为：', postion)
        setScrollH( postion.height )
        setScrollHeight( postion.top )
        clearTimeout(timer)
      }, 300)
    })
  
  },[end])
 
  const getData = (num)=>{
    setNum(num+1)
  }
  const {height} = props;
  const updatedList = llist.slice(0, end);
  // console.log(updatedList);

  const listLength = 720;
  if ( scrollHeight> (((more+1) *(1420-142*num))-420) || scrollH < 2000) {
    console.log('shuashuashua');
    const maxStartIndex = listLength - 1 - THRESHOLD;     // Maximum index value `start` can take
    const maxEndIndex = listLength - 1;                   // Maximum index value `end` can take
    const newEnd = (end + 10) <= maxEndIndex ? end + 10 : maxEndIndex;
    const newStart = (end - 5) <= maxStartIndex ? end - 5 : maxStartIndex;
    
    setLlist([...llist,{id:16+10*more},{id:17+10*more},{id:18+10*more},{id:19+10*more},{id:20+10*more},{id:21+10*more},{id:22+10*more},{id:23+10*more},{id:24+10*more},{id:25+10*more}])
    setMore(more+1)
    setStart(newStart)
    setEnd(newEnd)
  }
  // Scroll up
  // if ( scrollHeight> 1500 ) {
  //   const newEnd = end === THRESHOLD ? THRESHOLD : (end - 10 > THRESHOLD ? end - 10 : THRESHOLD);
  //   let newStart = start === 0 ? 0 : (start - 10 > 0 ? start - 10 : 0);
  //   setStart(newStart)
  //   setEnd(newEnd)
  // }
  

  return (
    <div style={{position: 'relative'}}>
      {updatedList.map((item, index) => {
        const top = (height * (index + start)) + 'px';
        // const id = item.id === 1 ? 'top' : (item.id === lastIndex ? 'bottom' : '');
        return (
            // <li className="li-card" key={item.key} style={{top}} ref={refVal} id={id}>{item.value}</li>
            // console.log(item.id-1)
          <ApiArt getData={getData.bind(num)} num={num} showcate2={showcate2} iitem={item.id-1} key={item.id-1} style={{top}} limit={item.id} id={item.id}></ApiArt>
        );
      })}
    </div>
  );
}
export { SlidingWindowScrollHook };
