import React,{ Component } from 'react'
import { getArticles } from './fake-api';

export default class Aart extends Component{
  constructor(){ 
    super()
    this.state = {
      art:[],
      success:false
    }
    async function helloAsync(){
      const a= await getArticles();
      
      console.log('asd',a);
      const b = a.data.articles[0].author_user_info.user_name
      const e = [b]
      return e
    }
    helloAsync().then((e)=>{
      this.setState({
        art:e[0],
        success:true},()=>{console.log(this.state.success)}
      )
    })
  }
  render(){
    
    const {aart} = this.props
    const {article_content} = aart[3]
    // console.log(article_content)
    
    return (
    <div dangerouslySetInnerHTML = {{__html:article_content}}></div>
    // <div></div>
    )
  }
  
}
