  //   console.log('iiii',newiitem);
  // }

// 页面
  render() {
    return (
      <div className="App">
        <header className="header">
          <NavTop/>
        </header>
        {/* <div>
          <div className='art1'></div>
          <div className='art2'>
            <Aart ></Aart>
            <Com ></Com>
          </div>
        </div> */}
        <main className='container main-container with-view-nav'>
          <div className='view timeline-index-view'>
            <Recom></Recom>
            <div id='Recomafter'/>

            <ShowArticle></ShowArticle>
          </div>
        </main>
        <div className='apiArt' id='i'>
        <SlidingWindowScrollHook height={195}/>
          {/* {
            this.state.iitem.map( iitem=>{
              return <ApiArt iitem={iitem} key={iitem} offset={this.state.hot.offset}></ApiArt>
            })
          } */}
        </div>

      </div>
    )
  }
}

export default App;
