import React from 'react'
// import ReactDOM from 'react-dom'
import { getCategories, getArticleById, getArticles, getCommentsByArticleId } from './fake-api';
// import Datetime from './componments/Datetime'
import NavTop from './componments/NavTop'
// import Aart from './componments/Aart'
// import Com from './componments/Com'
import ApiArt from './componments/ApiArt'
import Recom from './componments/Recom'
import ShowArticle from './componments/ShowArticle'
import './App.css'

class App extends React.Component{
  constructor(){ 
    super()
    this.state = {
      hot:{
        categoryId : 0, sortBy : 'hot', offset : 0, limit : 10
      },
      art:[],
      com:[],
      success:false
    }
    async function helloAsync(){
      const a= await getArticles();
      const c= await getCommentsByArticleId();

      console.log('asd',a);
      const b = a.data.articles[0].author_user_info.user_name
      const d = c.data.comments[0].comment_info.comment_content
      // // console.log('aasd',d);
      const e = [b,d]
      return e
    }
    helloAsync().then((e)=>{
      this.setState({
        art:e[0],com:e[1],
        success:true},()=>{console.log(this.state.success)}
      )
    })
    getCategories();
    getArticleById();
  }

// 页面
  render() {
    return (
      <div className="App">
        <header className="header">
          <NavTop/>
        </header>
        {/* <div>
          <div className='art1'></div>
          <div className='art2'>
            <Aart ></Aart>
            <Com ></Com>
          </div>
        </div> */}
        <main className='container main-container with-view-nav'>
          <div className='view timeline-index-view'>
          <Recom></Recom>
          <ShowArticle></ShowArticle>
          </div>
        </main>
        <div className='apiArt'>
          <ApiArt></ApiArt>
          <ApiArt></ApiArt>
          <ApiArt></ApiArt>
        </div>

      </div>
    )
  }
}

export default App;
