import React,{ Component } from 'react'
import { getArticles } from '../../fake-api';
import Datetime from '../Datetime'
// import SlidingWindowScrollHook from '../InfiniteScroll'

export default class ApiArt extends Component{
  constructor(props){ 
    super(props)
    this.state = {
      iitem:0,
      art:[],
      ctime:'',
      cateName:{
        first_category_name:'',
        second_category_name:''
      },
      brief_content:'',
      title:'',
      cover_image:'',
      digg_count:5,
      comment_count:8,
      collect_count:3,
      success:false,
      showcate2:this.props.showcate2
    }
    let _this = this
    async function helloAsync(){
      const { iitem , limit , showcate2 } = _this.props
      const a= await getArticles( 0, 'hot', 0 , limit );
      // console.log(_this.props);
      // console.log(iitem,a);
      const b = a.data.articles[iitem].author_user_info.user_name
      // console.log('aasd',b);
      const f = Datetime(a.data.articles[iitem].article_info.ctime*1000)
      const g = a.data.articles[iitem].category_info
      const h = a.data.articles[iitem].article_info
      // console.log(a.data.articles[0].article_info.ctime);
      // console.log(g,showcate2);
      const e = [b,'',f,g,h]
      return ((showcate2 === g.first_category_name || showcate2 === g.second_category_name || showcate2 === "全部") ? e : ['','','','',''])
    }
    helloAsync().then((e)=>{
      this.setState({
        art:e[0],ctime:e[2],
        cateName:{
          first_category_name:e[3].first_category_name,
          second_category_name:e[3].second_category_name},
        brief_content:e[4].brief_content,title:e[4].title,cover_image:e[4].cover_image,collect_count:e[4].collect_count,digg_count:e[4].digg_count,comment_count:e[4].comment_count,
        success:true},()=>{console.log(this.state.success)}
      )
    })
  }
  componentDidMount(){
    async function helloAsync(){
      const { iitem , limit , showcate2 } = this.props
      const a= await getArticles( 0, 'hot', 0 , limit );
      // console.log(_this.props);
      // console.log(iitem,a);
      const b = a.data.articles[iitem].author_user_info.user_name
      // console.log('aasd',b);
      const f = Datetime(a.data.articles[iitem].article_info.ctime*1000)
      const g = a.data.articles[iitem].category_info
      const h = a.data.articles[iitem].article_info
      // console.log(a.data.articles[0].article_info.ctime);
      // console.log(g,showcate2);
      const e = [b,'',f,g,h]
      return ((showcate2 === g.first_category_name || showcate2 === g.second_category_name || showcate2 === "全部") ? e : ['','','','',''])
    }
    helloAsync().then((e)=>{
      this.setState({
        art:e[0],ctime:e[2],
        cateName:{
          first_category_name:e[3].first_category_name,
          second_category_name:e[3].second_category_name},
        brief_content:e[4].brief_content,title:e[4].title,cover_image:e[4].cover_image,collect_count:e[4].collect_count,digg_count:e[4].digg_count,comment_count:e[4].comment_count,
        success:true},()=>{console.log(this.state.success)}
      )
    })
  }
  numChange=()=>{
    this.props.getData(this.props.num)
  }
  render(){
    const display2 = {
      width:this.state.cover_image ? '200px' : '335px'
    }
    return (
      <div>
      { 
        this.state.ctime === '' ? this.numChange :
        (<div className='apiArt2'>
          <div className='apiArt3'>
            <ul className='apiArtul'>
              <li className='apiArtli'>{this.state.art}</li>
              <li className='apiArtli'>{this.state.ctime}</li>
              <li className='apiArtli'>{this.state.cateName.first_category_name}&nbsp;&nbsp;·&nbsp;&nbsp;{this.state.cateName.second_category_name}</li>
            </ul>
            <div className='apiArt4'>
              <span className='apiArtspan1' style={display2}>{this.state.title}</span>
              {this.state.cover_image === "" ?  <div/>: <img src={this.state.cover_image} alt="img" />}
              <span className='apiArtspan2' style={display2}>{this.state.brief_content}</span>
              <ul className='apiArtul'>
                <li className='apiArtli2'>
                  <img src="//lf3-cdn-tos.bytescm.com/obj/static/xitu_juejin_web/img/view.1eda8fa.png" alt="eye" />
                  &nbsp;{this.state.collect_count}
                </li>
                <li className='apiArtli2'>
                <img src="data:image/png;base64," alt="like" />
                  &nbsp;{this.state.digg_count}
                </li>
                <li className='apiArtli2'>
                <img src="data:image/png;base64," alt="comments" />
                  &nbsp;{this.state.comment_count}
                </li>
                <li className='apiArtli2'></li>
              </ul>
            </div>
            
          </div>
        </div>)
      }
      </div> 
  )}
  
}
