import React,{ Component } from 'react'
import { getArticles } from '../../fake-api';

export default class Aart extends Component{
  constructor(){ 
    super()
    this.state = {
      art:[],
      success:false
    }
    async function helloAsync(){
      const a= await getArticles();
      
      console.log('asd',a);
      const b = a.data.articles[0]
      const c = b.article_content
      const d = b.author_user_info
      
      const e = [c]
      return e
    }
    helloAsync().then((e)=>{
      this.setState({
        art:e[0],
        success:true},()=>{console.log(this.state.success)}
      )
    })
  }
  render(){
    return (
    <div>
      <img src="" alt="" />
      <div dangerouslySetInnerHTML = {{__html:this.state.art}}></div>
    </div>
    )
  }
  
}
