import React,{ Component } from 'react'
import { getCommentsByArticleId } from '../../fake-api';

export default class Com extends Component{
  constructor(){ 
    super()
    this.state = {
      art:[],
      com:[],
      success:false
    }
    async function helloAsync(){
      const a= await getCommentsByArticleId();
      
      const b = a.data.comments[0]
      console.log('com',c)
      const c = b.comment_info.comment_content
      const d = b.user_info.avatar_large
      const f = b.user_info.user_name
      const g = b.user_info.description
      const e = [c,d,f,g]
      return e
    }
    helloAsync().then((e)=>{
      this.setState({
        com:e[0],img:e[1],user_name:e[2],description:e[3],
        success:true},()=>{console.log(this.state.success)}
      )
    })
  }
  render(){

    return (
      <div>
        <div>
        <img src={this.state.img} alt="" id="artimg"/>
      <div className='username'>
        <div className='user_name'>{this.state.user_name}</div>
        <div className='description'>{this.state.description}</div>
      </div>
        </div>
        {this.state.com}
      </div>
    )
  }
  
}
