import React,{ Component } from 'react'
import { getCommentsByArticleId } from '../../fake-api';

export default class Com extends Component{
  constructor(){ 
    super()
    this.state = {
      art:[],
      com:[],
      success:false
    }
    async function helloAsync(){
      const c= await getCommentsByArticleId();
      
      const d = c.data.comments[0].comment_info.comment_content
      // // console.log('aasd',d);
      // const f = Datetime(a.data.articles[0].article_info.ctime*1000)
      // const g = a.data.articles[0].category_info
      // const h = a.data.articles[0].article_info
      // console.log(a.data.articles[0].article_info.ctime);
      // console.log(g);
      const e = [d]
      return e
    }
    helloAsync().then((e)=>{
      this.setState({
        com:e[0],
        success:true},()=>{console.log(this.state.success)}
      )
    })
  }
  render(){

    return (
      <div>{this.state.com}</div>
    )
  }
  
}
