import React,{ Component } from 'react'

export default class Com extends Component{
  
  constructor(props){ 
    super(props)
    this.state={
      com:[],
      m:[]
    }

    const mmm=(props) => {
      this.setState(
        {com:this.props.com.comment_info},()=>{console.log('bbb',this.state.com)})
    }
    async function helloAsync(){
      setTimeout( function(){
        //5秒后实现的方法写在这个方法里面
        const mm = mmm()
        return mm
      },  1000 )
      
    }
    helloAsync().then(()=>{
      this.setState(
        {m:[]},()=>{console.log('bbb',this.state.com)}
      )
    })
  }
  
  render(){
    // console.log(this.props.com.comment_info.comment_content)
    // const comment_content = this.props.comment_content.comment_content
    // console.log(comment_content)
    
    return (
    <div>{this.state.com}</div>
    // <div></div>
    )
  }
  
}
