import React, { Component } from 'react'
import { getCategories } from '../../fake-api';

export default class ShowArticle extends Component{
  constructor(){ 
    super()
    this.state = {
      hot:{
        categoryId : 0, sortBy : 'hot', offset : 0, limit : 10
      },
      category_name:{name:''},
      active:false,
      success:false
    }
    async function helloAsync(){
      const a= await getCategories();
      
      console.log('recom',a);
      const b = a.data.categories[0].category_name
      const c = a.data.categories[1].category_name
      const d = a.data.categories[2].category_name
      const f = a.data.categories[3].category_name
      const g = a.data.categories[4].category_name
      const e = [b,c,d,f,g]
      return e
    }
    helloAsync().then((e)=>{
      this.setState({
        category_name:{name0:e[0],name1:e[1],name2:e[2],name3:e[3],name4:e[4]},
        success:true},()=>{console.log(this.state.success)}
      )
    })
    getCategories();
    
  }

  render(){
    return(
      <nav className='view-nav'>
        <div className='nav-list left'>
          <a href="/recommended" className="nav-item router-link-exact-active route-active active">
            <div className="category-popover-box">{this.state.category_name.name0}</div>
          </a>
          <a href="/backend" className={this.state.active===true?'nav-item router-link-exact-active route-active active':"nav-item"}><div>后端</div></a>
          <a href="/backend" className="nav-item"><div>前端</div></a>
          <a href="/backend" className="nav-item"><div>Android</div></a>
          <a href="/backend" className="nav-item"><div>iOS</div></a>
          <a href="/backend" className="nav-item"><div>人工智能</div></a>
          <a href="/backend" className="nav-item"><div>开发工具</div></a>
          <a href="/backend" className="nav-item"><div>代码人生</div></a>
          <a href="/backend" className="nav-item"><div>阅读</div></a>

        </div>
      </nav>
      
    )
  }
}
