import React from 'react'
// import ReactDOM from 'react-dom'
import { getArticles, getCommentsByArticleId } from './fake-api';
// import Datetime from './componments/Datetime'
import NavTop from './componments/NavTop'
import Aart from './componments/Aart'
// import ApiArt from './componments/ApiArt'
import Recom from './componments/Recom'
import ShowArticle from './componments/ShowArticle'
import TabBottom from './componments/TabBottom'
import { SlidingWindowScrollHook } from './componments/InfiniteScroll'
import './App.css'

class App extends React.Component{
  constructor(){ 
    super()
    this.state = {
      sortBy : 'hot',
      art:[],
      com:[],
      showcate:{name0:'',name1:'',name2:'',name3:'全部',categoryId:0,},
      showcate2: '全部',
      categoryId1:0,
      article_id:"6984908770149138446",
      active:false,
      success:false
    }

    async function helloAsync(){
      const a= await getArticles();
      const c= await getCommentsByArticleId();

      console.log('asd',a);
      const b = a.data.articles[0].author_user_info.user_name
      const d = c.data.comments[0].comment_info.comment_content
      // // console.log('aasd',d);
      const e = [b,d]
      return e
    }
    helloAsync().then((e)=>{
      this.setState({
        art:e[0],com:e[1],
        success:true},()=>{}
      )
    })
  }
  getData(msg0,msg1,msg2,categoryId){
    this.setState({
      showcate: {name0:msg0,name1:msg1,name2:msg2,categoryId}
    });
  }
  getData2(showcate2,categoryId1){
    this.setState({
      showcate2,categoryId1
    });
  }
  getData3(sortBy){
    this.setState({sortBy})
  }
  getData4(article_id){
    this.setState({article_id,active:!this.state.active})
  }
  // shouldComponentUpdate(nextProps, nextState) {
  //   return true;  
  // }
  render() {
    return (
      <div className="App">
        <header className="header">
          <NavTop/>
        </header>
        <div>
          <div className='art1'></div>
          <div className='containerr article' style={this.state.active!==true?{display:'none'}:{display:'block'}}>
            <div className='art2'>
              <Aart article_id={this.state.article_id}></Aart>
            </div>
          </div>
        </div>
        <div style={this.state.active===true?{display:'none'}:{display:'block'}}>
          <main className='container main-container with-view-nav'>
            <div className='view timeline-index-view'>
              <Recom getdata={this.getData.bind(this)}></Recom>
              <ShowArticle getdata2={this.getData2.bind(this)} showcate={this.state.showcate}></ShowArticle>
              <TabBottom getdata3={this.getData3.bind(this)}></TabBottom>
            </div>
          </main>
          <div style={this.state.active===true?{display:'none'}:{display:'block'}} className='apiArt' id='i' >
            <SlidingWindowScrollHook getdata4={this.getData4.bind(this)} sortBy={this.state.sortBy} height={195} categoryId1={this.state.categoryId1} showcate={this.state.showcate} />
          </div>
        </div>
      </div>
    )
  }
}

export default App;
