export default class ShowArticle extends Component{
  state={
    active:0
  }
  handleClick(e,sortBy){
    this.setState({ active: e })
    this.props.getdata3(sortBy)
  }
  handleHistory(e){
    this.setState({ active: e })
    this.props.getdata5()
  }
  aaa=()=>{
    console.log('123465');
  }
  componentDidMount(){
    this.props.ref(this)
  }
