import React, { Component } from 'react'

export default class ShowArticle extends Component{
  state={
    active:0
  }
  handleChange(e,msg){
    this.setState({ active: e });
    this.props.getdata2(msg)
  }
  render(){
    return(
      
      <div className="timeline-container">
        {console.log('3345',this.props)}
        
        <nav className="tag-nav tag-navigator">
          <ul className="nav-list tag-list">
        {this.props.showcate.name0 === '' ? <li></li> :
            <li style='{display:none}' className={this.state.active===0?"nav-item tag router-link-exact-active route-active active":"nav-item tag"} onClick={() => {this.handleChange(0,this.props.showcate.name0)}}>{this.props.showcate.name0}</li>
          }<li className={this.state.active===1?"nav-item tag router-link-exact-active route-active active":"nav-item tag"} onClick={() => {this.handleChange(1,this.props.showcate.name1)}}>{this.props.showcate.name1}</li>
            <li className={this.state.active===2?"nav-item tag router-link-exact-active route-active active":"nav-item tag"} onClick={() => {this.handleChange(2,this.props.showcate.name2)}}>{this.props.showcate.name2}</li>
          
          </ul>
        </nav>
        
        <div className="timeline-content">
          <div className="timeline-entry-list">
            <header className="list-header">
              <nav className="list-nav">
                {/* 下方导航页面 */}
                <div className='nav-list left'>
                  <a href="/recommended" className="nav-item router-link-exact-active route-active active">
                    <div className="category-popover-box">热门</div>
                  </a>
                  <a href="/backend" className="nav-item"><div>最新</div></a>
                  <a href="/backend" className="nav-item"><div>热榜</div></a>
                </div>
              </nav>
            </header>
          </div>
        </div>
      </div>
      
    )
  }
}
